// Try to get the object from local or return an empty string if nothing found ""
func fromLocal(ctx *fiber.Ctx, key string) string {
	keyValue := ctx.Locals(key)
	value := ""
	if keyValue != nil {
		value = keyValue.(string)
	}
	return value
}

